/**
 * The margin size for each side.
 */
export interface Margin {
  /**
   * The top margin in pixels.
   */
  top?: number;

  /**
   * The right margin in pixels.
   */
  right?: number;

  /**
   * The bottom margin in pixels.
   */
  bottom?: number;

  /**
   * The left margin in pixels.
   */
  left?: number;
}
